-- holders --local _, lib = ...local db = ncUIdb["general"]local addon = CreateFrame("Frame")local noop = function() endlocal callbacks = {	["update"] = {},	["events"] = {},}-- localized for the speed --local floor = math.floor-- constants --lib.RESOLUTION = GetCVar("gxResolution")lib.RESOWIDTH, lib.RESOHEIGHT = string.match(lib.RESOLUTION, "(%d+)x(%d+)")lib.PP = 768/lib.RESOHEIGHT/db.uiscalelib.BACKDROP = {	bgFile = ncUIdb["media"].solid,	edgeFile = ncUIdb["media"].solid,	tile = false, tileSize = 0, edgeSize = lib.PP,	insets = {		left = -lib.PP,		right = -lib.PP,		top = -lib.PP,		bottom = -lib.PP,	}}-- hooks & events --local function fire(obj, script, ...)	if not obj.GetScript then return false end		local handler = obj:GetScript(script)	if handler then		handler(obj, script, ...)		return true	end	return falseendlocal function hook(tab, func, mode, hookfunc)	if type(tab)=="table" then		if not tab[func] then return end	else		hookfunc = mode		mode = func		func = tab		tab = _G	end	local orig = tab[func]	if mode=="pre" then		tab[func] = function(...)			hookfunc(...)			orig(...)		end	elseif mode=="pre-feed" then		tab[func] = function(...)			orig(hookfunc(...))		end	elseif mode=="post-feed" then		tab[func] = function(...)			hookfunc(orig(...))		end	else		tab[func] = function(...)			orig(...)			hookfunc(...)		end	endendlocal function registerevent(event, handler)	if not callbacks.events[event] then		callbacks.events[event] = {}		addon:RegisterEvent(event)	end	local index = #callbacks.events[event] + 1	callbacks.events[event][index] = handler	return indexendlocal function unregisterevent(event, index)	if not callbacks.events[event] then return end	callbacks.events[event][index] = nil	if not next(callbacks.events[event]) then		callbacks.events[event] = nil		addon:UnregisterEvent(event)	endendaddon:SetScript("OnUpdate", function(_, elapsed)	for index, handler in next, callbacks.update do		handler(elapsed)	endend)addon:SetScript("OnEvent", function(_, event, ...)	if not callbacks.events[event] then return end	for index, handler in next, callbacks.events[event] do		handler(event, ...)	endend)local function fireevent(obj, event, ...)	if type(obj)=="number" then		if not callbacks.events[event] or not callbacks.events[event][obj] then			return false		end		callbacks.events[event][obj](event, ...)		return true	end	if not obj.GetScript then return false end	local handler = obj:GetScript("OnEvent")	if handler then		handler(obj, event, ...)		return true	end	return falseendlocal function registerupdate(handler)	if type(handler)~="function" then return end	local index = #callbacks.update	callbacks.update[index] = handler	return indexendlocal function unregisterupdate(index)	callbacks.update[index] = nilend-- useful lua extensions --local function rawunpack(tab)	local temp = {}	local index = 1	for _, val in next, tab do		temp[index] = val		index = index + 1	end	return unpack(temp)end-- math ---- localization --local hash = "f528764d%624*db1!$!@29b32c(@)21fbca0cb8d6" -- no one wants to translate thislib.L = setmetatable({[hash]={}}, {__index = function(tab, i)	local locale=GetLocale()	return lib.L[hash][locale] and tab[hash][locale][i] or iend})local function localize(locale, translation)	lib.L[hash][locale] = lib.L[hash][locale] or {}	for key, val in next, translation do		lib.L[hash][locale][key] = val	endend-- graphics & frames --local function destroy(obj)	if not obj.Show then return end	obj.Show = noop	obj:Hide()	if obj.UnregisterAllEvents then		obj:UnregisterAllEvents()	endendlocal function lock(obj)	obj.ClearAllPoints = noop	obj.SetPoint = noop	obj.SetAllPoints = noopendlocal function unlock(obj)	obj.ClearAllPoints = nil	obj.SetPoint = nil	obj.SetAllPoints = nilendlocal function round(num)	return floor(num + .5)endlocal function scale(px, ign)	if ign then return end	return PP * floor(px + .5)endlocal function spawnfont(obj, name, layer)	return obj:CreateFontString(name, layer or "OVERLAY", "ncUIfont")endlocal function settemplate(obj)	obj:SetBackdrop(lib.BACKDROP)	obj:SetBackdropColor(unpack(db.backdrop))	obj:SetBackdropBorderColor(unpack(db.border))endlocal function toggle(obj)	if not obj.Hide then return end	if obj:IsShown() then		obj:Hide()		return false	end	obj:Show()	return trueendlocal function settoolbox(obj)	obj.SetTemplate = settemplate	obj.SpawnFont = spawnfont	obj.Destroy = destroy	obj.Lock = lock	obj.Unlock = unlock	obj.Toggle = toggle	if obj.GetScript then		obj.Fire = fire		if obj.RegisterEvent then			obj.FireEvent = fireevent		end	end		hook(obj, "SetSize", "pre-feed", function(w, h, ign)		local ign = ign		if not tonumber(h) then			ign = h			h = w		end		return scale(w, ign), scale(h, ign)	end)	hook(obj, "SetWidth", "pre-feed", function(w, ign) return scale(w, ign) end)	hook(obj, "SetHeight", "pre-feed", function(h, ign) return scale(h, ign) end)	hook(obj, "SetPoint", "pre-feed", function(...)		local point = {...}		local num = #point		local ign = point[num]		for i=1, num do			local px = tonumber(point[i])			if px then				point[i] = scale(px, ign)			end		end		return unpack(point)	end)endlocal function createframe(...)	local obj = CreateFrame(...)	settoolbox(obj)	return objend-- lib householding --local function set(name, func)	lib[name] = function(self, ...) func(...) endendset("Fire", fire)set("Hook", hook)set("RegisterEvent", registerevent)set("UnregisterEvent", unregisterevent)set("FireEvent", fireevent)set("RegisterUpdate", registerupdate)set("UnregisterUpdate", unregisterupdate)set("RawUnpack", rawunpack)set("Localize", localize)set("CreateFrame", createframe)set("SetToolbox", settoolbox)set("Scale", scale)set("SetTemplate", settemplate)set("SpawnFont", spawnfont)set("Destroy", destroy)set("Lock", lock)set("Unlock", unlock)set("Toggle", toggle)